DELIMITER ;;
CREATE DEFINER=`gspeluser`@`%` PROCEDURE `proc_pemarkahan_modul`(IN `giatmara` int, IN `kursus` int, IN `jeniskursus` varchar(16), IN `sesi` int)
BEGIN
	(SELECT
ref_giatmara.nama_giatmara,
ref_kursus.nama_kursus,
ref_kursus.jenis_kursus AS jenis,
markah_modul.id_modul,
pelatih.jenis_pelatih AS jenis_kursus,
ref_intake.nama_intake,
ref_modul.kod_modul,
ref_modul.nama_modul,
COUNT(pelatih.id_pelatih) AS jumlah_pelatih,
	SUM(
		CASE
		WHEN markah_modul.status_terampil = 1 THEN
			1
		ELSE
			0
		END
	) AS terampil,
	SUM(
		CASE
		WHEN markah_modul.status_terampil = 2 THEN
			1
		ELSE
			0
		END
	) AS tidak_terampil,
Sum(CASE WHEN (markah_modul.status_isi_markah = 0) THEN 1 ELSE 0 END) AS belum_isi,
Sum(CASE WHEN (markah_modul.status_isi_markah = 1) THEN 1 ELSE 0 END) AS telah_disimpan,
Sum(CASE WHEN (markah_modul.status_isi_markah = 2) THEN 1 ELSE 0 END) AS hantar_ke_pengurus,
Sum(CASE WHEN (markah_modul.status_isi_markah = 3) THEN 1 ELSE 0 END) AS dikembalikan,
Sum(CASE WHEN (markah_modul.status_isi_markah = 4) THEN 1 ELSE 0 END) AS hantar_ke_pgn,
Sum(CASE WHEN (markah_modul.status_isi_markah = 5) THEN 1 ELSE 0 END) AS dikembalikan_oleh_pgn,
Sum(CASE WHEN (markah_modul.status_isi_markah = 6) THEN 1 ELSE 0 END) AS hantar_ke_ppkl,
Sum(CASE WHEN (markah_modul.status_isi_markah = 7) THEN 1 ELSE 0 END) AS disahkan_oleh_ppkl,
Sum(CASE WHEN (markah_modul.status_isi_markah = 8) THEN 1 ELSE 0 END) AS dikembalikan_oleh_ppkl,
settings_tawaran_kursus.id_giatmara,
settings_tawaran_kursus.id AS id_kursus,
ref_kursus.id AS id_ref_kursus,
settings_tawaran_kursus.id_intake
FROM
settings_tawaran_kursus
LEFT JOIN (SELECT * FROM pelatih WHERE (
			CASE
			WHEN jeniskursus = "" THEN
				TRUE
			ELSE
				pelatih.jenis_pelatih = jeniskursus
			END
		)
) pelatih ON pelatih.id_kursus = settings_tawaran_kursus.id
INNER JOIN ref_kursus ON settings_tawaran_kursus.id_kursus = ref_kursus.id
INNER JOIN ref_modul ON ref_modul.id_kursus = settings_tawaran_kursus.id_kursus
INNER JOIN ref_giatmara ON settings_tawaran_kursus.id_giatmara = ref_giatmara.id AND pelatih.id_giatmara = ref_giatmara.id
INNER JOIN ref_intake ON settings_tawaran_kursus.id_intake = ref_intake.id
LEFT JOIN (SELECT * FROM markah_modul WHERE markah_modul.id_modul IS NOT NULL) markah_modul ON markah_modul.id_pelatih = pelatih.id_pelatih AND ref_modul.id_modul = markah_modul.id_modul AND settings_tawaran_kursus.id = markah_modul.id_kursus
WHERE
	settings_tawaran_kursus.id_giatmara = giatmara
AND settings_tawaran_kursus.id_kursus = kursus
AND settings_tawaran_kursus.status = 1
-- AND settings_tawaran_kursus.id_intake = `sesi`
GROUP BY
