Sum(CASE WHEN (markah_modul_2.status_isi_markah = 5) THEN 1 ELSE 0 END) AS dikembalikan_oleh_pgn,
Sum(CASE WHEN (markah_modul_2.status_isi_markah = 6) THEN 1 ELSE 0 END) AS hantar_ke_ppkl,
Sum(CASE WHEN (markah_modul_2.status_isi_markah = 7) THEN 1 ELSE 0 END) AS disahkan_oleh_ppkl,
Sum(CASE WHEN (markah_modul_2.status_isi_markah = 8) THEN 1 ELSE 0 END) AS dikembalikan_oleh_ppkl,
settings_tawaran_kursus.id_giatmara,
