INNER JOIN (
	SELECT
		*
	FROM
		pelatih
	WHERE
		(
			CASE
			WHEN jeniskursus = "" THEN
				TRUE
			ELSE
				pelatih.jenis_pelatih = jeniskursus
			END
		)
) t1 ON t1.id_kursus = settings_tawaran_kursus.id AND t1.id_giatmara = settings_tawaran_kursus.id_giatmara
LEFT JOIN markah_modul_2 ON t1.id_pelatih = markah_modul_2.id_pelatih AND t1.id_kursus = markah_modul_2.id_kursus
INNER JOIN ref_kursus ON settings_tawaran_kursus.id_kursus = ref_kursus.id
INNER JOIN ref_giatmara ON settings_tawaran_kursus.id_giatmara = ref_giatmara.id
INNER JOIN ref_intake ON settings_tawaran_kursus.id_intake = ref_intake.id
WHERE
settings_tawaran_kursus.id_giatmara = giatmara AND
settings_tawaran_kursus.id_kursus = kursus AND
settings_tawaran_kursus.status = 1 AND
settings_tawaran_kursus.id_intake = sesi
GROUP BY
