DROP FUNCTION IF EXISTS `get_no_pelatih_baru`;
DELIMITER ;;
CREATE DEFINER=`gspeluser`@`%` FUNCTION `get_no_pelatih_baru`() RETURNS varchar(45) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci
BEGIN
	DECLARE hasil VARCHAR(45);

	SET hasil = (SELECT CONCAT(DATE_FORMAT(NOW(), "%Y%m"), LPAD(COALESCE(MAX(CAST(SUBSTR(no_pelatih,7,4) AS signed)), NULL, 0) + 1,4,'0')) AS no_pelatih_baru FROM pelatih WHERE SUBSTR(no_pelatih,1,6) = DATE_FORMAT(NOW(), "%Y%m"));

	RETURN hasil;
END
;;
